mapper.select:
SELECT COUNT(*)
FROM chat_list
WHERE from_id = #{userId}
   OR to_id = #{userId}

select *
from chat_list
where from_id = #{userId}
   or to_id = #{userId}
ORDER BY last_time DESC
    LIMIT #{pvo.offsetPoint}, #{pvo.onePageRecord}

SELECT COUNT(*)
FROM message
WHERE chat_id = #{chatId}
  AND is_read = 0
  AND is_from_id != #{isFromId}

SELECT profile_img
FROM users
WHERE user_id = #{userId}

SELECT nation
FROM users
WHERE user_id = #{userId}

SELECT user_id AS userid, user_name AS username, email, nation, sex, lang, profile_img, profile_content
FROM users
WHERE user_id != #{userId}
  AND NOT EXISTS (
    SELECT 1
    FROM chat_list
    WHERE (from_id = #{userId} AND to_id = users.user_id)
   OR (to_id = #{userId} AND from_id = users.user_id)
    )
    LIMIT #{pvo.offsetPoint}, #{pvo.onePageRecord}

mapper.update:
UPDATE chat_list
SET last_content = #{last_content}
WHERE chat_id = #{chatId}

mapper.select:
SELECT symmetric_key
FROM chat_encryption_keys
WHERE chat_id = #{chatId} AND user_id = #{userId}

SELECT is_from_id, chat_id
FROM message
WHERE msg_id = #{msgId}

SELECT trans_content
FROM msg_trans
WHERE msg_id = #{msgId}
